function transformData(oldData) {
    const newData = {
        routes: []
    };

    const regionMap = new Map();

    oldData.data.forEach(route => {
        const fromRegionName = route.From_Station.System.Region.Region;
        const fromSystemName = route.From_Station.System.System;
        const fromStationName = route.From_Station.Station;

        const toRegionName = route.To_Station.System.Region.Region;
        const toSystemName = route.To_Station.System.System;
        const toStationName = route.To_Station.Station;

        const pricing = route.Pricing;
        const allowRush = route.AllowRush;

        const reward = pricing.Reward;
        const limits = pricing.Limit;

        const targetStation = {
            system: toSystemName,
            station: toStationName,
            reward: {
                volume: parseFloat(reward.Volume),
                collateral: parseFloat(reward.Collateral)
            },
            limits: {
                volume: parseFloat(limits.Volume),
                collateral: parseFloat(limits.Collateral)
            },
            minimumReward: parseInt(reward.Minimum, 10),
            allowRush: allowRush
        };

        // Handle region mapping
        if (!regionMap.has(fromRegionName)) {
            regionMap.set(fromRegionName, { region: fromRegionName, systems: new Map() });
        }

        const fromRegion = regionMap.get(fromRegionName);

        if (!fromRegion.systems.has(fromSystemName)) {
            fromRegion.systems.set(fromSystemName, {
                system: fromSystemName,
                station: fromStationName,
                regions: new Map()
            });
        }

        const fromSystem = fromRegion.systems.get(fromSystemName);

        if (!fromSystem.regions.has(toRegionName)) {
            fromSystem.regions.set(toRegionName, {
                region: toRegionName,
                systems: []
            });
        }

        const toRegion = fromSystem.regions.get(toRegionName);
        toRegion.systems.push(targetStation);
    });

    // Build final structure
    regionMap.forEach(region => {
        const regionEntry = {
            region: region.region,
            systems: []
        };

        region.systems.forEach(system => {
            const systemEntry = {
                system: system.system,
                station: system.station,
                regions: []
            };

            system.regions.forEach(subRegion => {
                systemEntry.regions.push({
                    region: subRegion.region,
                    systems: subRegion.systems
                });
            });

            regionEntry.systems.push(systemEntry);
        });

        newData.routes.push(regionEntry);
    });

    return newData;
}

