var allContracts = []
for (var i in msg.contracts) {
    for (var j in msg.contracts[i]) {
        allContracts.push(msg.contracts[i][j])
    }
}

var validContracts = allContracts.filter(contract => contract.type == "courier").filter(contract => contract.status == "outstanding")

var validContractsProgress = allContracts.filter(contract => contract.type == "courier").filter(contract => contract.status == "in_progress")

var last100Contracts = allContracts.filter(contract => contract.type == "courier").filter(contract => contract.status == "finished").sort((a, b) => {
    if (a.date_completed < b.date_completed)
    {
        return 1
    }
    if (b.date_completed < a.date_completed) {
        return -1
    }
    return 0
}).slice(0, 100)

var last100MJJitaContracts = allContracts.filter(contract => contract.type == "courier").filter(contract => contract.status == "finished")
    .filter(contract => (contract.end_location_id == 1038457641673 || contract.end_location_id == 60003760) && (contract.start_location_id == 1038457641673 || contract.start_location_id == 60003760)).sort((a, b) => {
    if (a.date_completed < b.date_completed) {
        return 1
    }
    if (b.date_completed < a.date_completed) {
        return -1
    }
    return 0
}).slice(0, 100)

var contractsTime = 0
for (var i in last100Contracts)
{
    var completeDate = new Date(last100Contracts[i].date_completed)
    var endDate = new Date(last100Contracts[i].date_issued)
    contractsTime = contractsTime + (completeDate.valueOf() - endDate.valueOf())
}

var mJJitacontractsTime = 0
for (var i in last100MJJitaContracts) {
    var completeDate = new Date(last100MJJitaContracts[i].date_completed)
    var endDate = new Date(last100MJJitaContracts[i].date_issued)
    mJJitacontractsTime = mJJitacontractsTime + (completeDate.valueOf() - endDate.valueOf())
}

var Last100ContractsAVGTime = contractsTime / 100

var Last100MJJitaContractsAVGTime = mJJitacontractsTime / 100

function msToTime(s) {
    var ms = s % 1000;
    s = (s - ms) / 1000;
    var secs = s % 60;
    s = (s - secs) / 60;
    var mins = s % 60;
    var hrs = (s - mins) / 60;

    //return hrs + ':' + mins + ':' + secs;
    return hrs;
}

msg.payload = 
{
